class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: 'xuanqiyan',
            flag: true,
        }
        this.nameInputRef = React.createRef() // 创建 ref
        this.fileInputRef = React.createRef()
    }
    render() {
        //非受控组件 input 和 name属性 没有关系
   //      return <div>
